# test_gemini_config.py\n\nimport os\nimport dspy\n\n# Set the API key (for this session)\nos.environ["GOOGLE_API_KEY"] = "YOUR_GOOGLE_API_KEY_HERE"\n\ndef test_gemini_configuration():\n    \"\"\"Test if we can configure DSPy with Google Gemini Pro.\"\"\"\n    try:\n        print(\"Attempting to configure DSPy with Google Gemini Pro...\")\n        \n        # Configure DSPy with Gemini Pro\n        lm = dspy.LM('google/gemini-pro')\n        dspy.configure(lm=lm)\n        \n        print(\"SUCCESS: DSPy configured with Google Gemini Pro\")\n        return True\n    except Exception as e:\n        print(f\"ERROR: Failed to configure Gemini API: {e}\")\n        return False\n\ndef test_simple_query():\n    \"\"\"Test a simple query with the configured model.\"\"\"\n    try:\n        print(\"\\nTesting simple query with Gemini Pro...\")\n        \n        # Simple test prompt\n        response = dspy.Predict(\"What is the sentiment of this review?\")(review=\"This movie is great!\")\n        \n        print(f\"Response: {response}\")\n        return True\n    except Exception as e:\n        print(f\"ERROR: Failed to execute simple query: {e}\")\n        return False\n\nif __name__ == \"__main__\":\n    print(\"Testing Google Gemini API integration with DSPy\")\n    print(\"=\" * 50)\n    \n    if test_gemini_configuration():\n        test_simple_query()